mapper.select:
SELECT id, name, parent_id, category_no
FROM CATEGORIES
WHERE id = #{id}

mapper.update:
UPDATE CATEGORIES
SET
    name       = #{name},
    parent_id  = #{parentId, jdbcType=NUMERIC}
WHERE id = #{id}

UPDATE CATEGORIES
SET category_no = (
    SELECT rn FROM (
                       SELECT id, ROW_NUMBER() OVER (ORDER BY category_no) AS rn
                       FROM CATEGORIES WHERE parent_id = #{parentId}
                   ) t WHERE t.id = CATEGORIES.id
)
WHERE parent_id = #{parentId}
