mapper.select:
SELECT oi.id AS order_item_id, p.id AS id, p.product_code AS product_code, p.product_name AS product_name, po.option_name AS option_name, oi.quantity AS quantity, p.price AS unitPrice,
/* 단가 기준 할인금액(금액/퍼센트 모두 흡수) */ CASE WHEN p.discount BETWEEN 0 AND 100 THEN ROUND(p.price * p.discount / 100) WHEN p.discount > 100 THEN p.discount ELSE 0 END AS unitDiscount,
/* 표시용 할인율(%): 금액 기준으로 역산 */ CASE WHEN p.price > 0 THEN ROUND( (CASE WHEN p.discount BETWEEN 0 AND 100 THEN ROUND(p.price * p.discount / 100) WHEN p.discount > 100 THEN p.discount ELSE 0 END) / p.price * 100 ) ELSE 0 END AS discountRate,
img.url AS url, p.delichar AS delichar FROM order_items oi JOIN orders o ON o.id = oi.orders_id JOIN products p ON p.id = oi.products_id LEFT JOIN product_options po ON po.id = oi.product_options_id LEFT JOIN product_images img ON img.products_id = p.id AND img.is_main='Y' WHERE o.users_id = #{userId} AND o.status = '결제대기' ORDER BY oi.id DESC

mapper.update:
UPDATE coupons
SET status = '종료',
used_order_id = #{orderId}   
WHERE id = #{couponId}
AND users_id = #{userId}
AND status = '발급중'

UPDATE orders o
SET o.total_amount = (
SELECT NVL(SUM(NVL(oi.price,0) * NVL(oi.quantity,0)), 0)
FROM order_items oi
WHERE oi.orders_id = #{orderId}
)
WHERE o.id = #{orderId}

UPDATE products
SET stock = stock - #{qty}
WHERE id = #{productId}
AND stock >= #{qty}

mapper.delete:
DELETE FROM order_items
WHERE orders_id IN (
SELECT id FROM orders
WHERE users_id=#{userId}
AND status='결제대기'
)

mapper.select:
SELECT oi.id AS order_item_id, pr.id AS id, pr.product_code AS product_code, pr.product_name AS product_name, po.option_name AS option_name, NVL(oi.quantity, 0) AS quantity, NVL(pr.price, 0) AS unitPrice,
/* 표시용 할인율(%): 금액 기준 역산 */ CASE WHEN pr.discount BETWEEN 0 AND 100 THEN ROUND(pr.discount) WHEN pr.discount > 100 AND NVL(pr.price,0) > 0 THEN ROUND((pr.discount / pr.price) * 100) ELSE 0 END AS discountRate,
/* 단가 기준 할인금액(클램프) */ LEAST( GREATEST( CASE WHEN pr.discount BETWEEN 0 AND 100 THEN ROUND(NVL(pr.price,0) * (pr.discount / 100.0)) WHEN pr.discount > 100 THEN ROUND(pr.discount) ELSE 0 END, 0 ), NVL(pr.price,0) ) AS unitDiscount,
/* 단가 판매가 */ ( NVL(pr.price,0) - LEAST( GREATEST( CASE WHEN pr.discount BETWEEN 0 AND 100 THEN ROUND(NVL(pr.price,0) * (pr.discount / 100.0)) WHEN pr.discount > 100 THEN ROUND(pr.discount) ELSE 0 END, 0 ), NVL(pr.price,0) ) ) AS unitSalePrice,
/* 라인합계 */ ( ( NVL(pr.price,0) - LEAST( GREATEST( CASE WHEN pr.discount BETWEEN 0 AND 100 THEN ROUND(NVL(pr.price,0) * (pr.discount / 100.0)) WHEN pr.discount > 100 THEN ROUND(pr.discount) ELSE 0 END, 0 ), NVL(pr.price,0) ) ) * NVL(oi.quantity,0) ) AS lineTotal,
img.url AS url, CAST(pr.delichar AS NUMBER) AS delichar, CASE WHEN NVL(pr.delichar,0) = 0 THEN 1 ELSE 0 END AS freeShipping
FROM order_items oi JOIN products pr ON pr.id = oi.products_id LEFT JOIN product_options po ON po.id = oi.product_options_id LEFT JOIN product_images img ON img.products_id = pr.id AND img.is_main = 'Y' WHERE oi.orders_id = #{orderId} ORDER BY oi.id

SELECT /* 주문/결제/구매자 */ o.order_code AS orderCode, p.method AS payMethod, u.name AS buyerName, u.phone AS buyerPhone, TO_CHAR(o.created_at, 'YYYY-MM-DD HH24:MI:SS') AS completedAt,
/* 배송지(orders_id에 저장된 스냅샷) — ProductCompleteDTO.shipping 으로 매핑 */ d.recipient AS "shipping.recipient", NVL(d.delicom, u.phone) AS "shipping.delicom", d.address AS "shipping.address", d.address2 AS "shipping.address2", d.delichar AS "shipping.delichar" -- 옵션
FROM orders o JOIN users u ON u.id = o.users_id LEFT JOIN payments p ON p.orders_id = o.id LEFT JOIN deliveries d ON d.orders_id = o.id WHERE o.id = #{orderId}

mapper.delete:
DELETE FROM order_items
WHERE orders_id = #{orderId}
AND id IN

    #{id}

mapper.select:
SELECT /* 총 상품금액: 정가 × 수량 */ SUM( NVL(p.price,0) * NVL(oi.quantity,0) ) AS subtotalAmount,
/* 총 할인금액: (단가 할인액) × 수량 */ SUM( LEAST( GREATEST( CASE WHEN p.discount BETWEEN 0 AND 100 THEN ROUND(NVL(p.price,0) * (p.discount/100.0)) WHEN p.discount > 100 THEN ROUND(p.discount) ELSE 0 END, 0 ), NVL(p.price,0) ) * NVL(oi.quantity,0) ) AS discountAmount,
/* 배송비 합계: 각 라인의 상품 배송비를 1회씩 합산(수량과 무관) */ SUM( NVL(p.delichar,0) ) AS delichar FROM order_items oi JOIN products p ON p.id = oi.products_id WHERE oi.orders_id = #{orderId}
